import React from 'react';
import { useTheme } from '@material-ui/core/styles';
import { LineChart, Line, XAxis, YAxis, Label, ResponsiveContainer } from 'recharts';
import Title from './Title';
import {Helmet} from "react-helmet";
import ScriptTag from 'react-script-tag';

export default function Chart(view) {
    const theme = useTheme();
    const getViewNumber = (viewName) => {
        console.log(viewName)
        switch (viewName) {
            case "Aave":
                return 7278;
            case "Compound":
                return 5692;
            case "Uniswap":
                return 7083;
            default:
                return 0;
        }
    }

    return (
        <React.Fragment>
            <Title>Today's Market</Title>
            <div className="coinmarketcap-currency-widget" data-currencyid={getViewNumber(view)} data-base="USD" data-secondary=""
                 data-ticker="true" data-rank="true" data-marketcap="true" data-volume="true" data-statsticker="true"
                 data-stats="USD">
                <Helmet>
                    <script src="https://jshosting.s3-us-west-2.amazonaws.com/library.js" type="text/javascript" />
                </Helmet>
            </div>
        </React.Fragment>
    );
}
