import React, {useEffect} from 'react';
import { useTheme } from '@material-ui/core/styles';
import { LineChart, Line, XAxis, YAxis, Label, ResponsiveContainer } from 'recharts';
import Title from './Title';
import {Helmet} from "react-helmet";
import ScriptTag from 'react-script-tag';
import {string} from "prop-types";

export default class Chart extends React.Component {
    constructor(props) {
        super(props);
        console.log(props)
        this.state = {
            view: this.props.viewName
        };
    }

    getViewNumber = () => {
        if (this.state.view === "Aave") {
            return "7278"
        } else if (this.state.view === "Compound") {
            return "5692"
        } else {
            return "7083"
        }
    }

    setNewView = () => {
        if (this.state.view !== this.props.viewName) {
            this.setState(
                this.state.view = this.props.viewName
            )
        }
    }


    render() {
        const id = this.setNewView();
        return (
            <div>
                <Title>Today's Market</Title>
                <div className="coinmarketcap-currency-widget" data-currencyid="7083" data-base="USD" data-secondary=""
                     data-ticker="true" data-rank="true" data-marketcap="true" data-volume="true" data-statsticker="true"
                     data-stats="USD">
                    <Helmet>
                        <script src="https://jshosting.s3-us-west-2.amazonaws.com/library.js" type="text/javascript" />
                    </Helmet>
                </div>
            </div>
        );
    }

}
